// Importe React
import React from 'react';

// Définit le composant TodoList avec des props pour les todos, completeTodo, et removeTodo
const TodoList = ({ todos, completeTodo, removeTodo }) => {
  // Rendu de la liste des todos avec des boutons pour marquer comme terminé ou supprimer
  return (
    <ul>
      {todos.map((todo, index) => (
        <li key={index}>
          <span style={{ textDecoration: todo.completed ? 'line-through' : 'none' }}>
            {todo.text}
          </span>
          <button onClick={() => completeTodo(index)}>Terminé</button>
          <button onClick={() => removeTodo(index)}>Supprimer</button>
        </li>
      ))}
    </ul>
  );
};

// Exporte le composant TodoList
export default TodoList;
